# -*- coding: utf-8 -*-
from plugins import pluginimport globalvclass pluginClass(plugin):	def gettype(self):		return "command"	def action(self, complete):		plugins=globalv.loadedPlugins.keys()		plugins+=globalv.loadedSpecial.keys()		plugins+=globalv.loadedRealtime.keys()		joiner=", "+globalv.commandCharacter		msg="Loaded plugins are: "+globalv.commandCharacter+joiner.join(plugins)+". For more information, try the "+globalv.commandCharacter+"help command."		return ["PRIVMSG $C$ :"+msg]	def describe(self, complete):		return ["PRIVMSG $C$ :I am the !say module","PRIVMSG $C$ :Usage:","PRIVMSG $C$ :!say [input]"]