# -*- coding: utf-8 -*-
from plugins import pluginimport globalvclass pluginClass(plugin):	def gettype(self):		return "command"	def action(self, complete):		msg=complete.message()		if msg.split()[0].lower()=="decode":			msg=' '.join(msg.split()[1:]).decode("rot13")		elif msg.split()[0].lower()=="encode":			msg=' '.join(msg.split()[1:]).encode("rot13")		else:			msg=msg.encode("rot13")				return ["PRIVMSG $C$ :"+msg]	def describe(self, complete):		msg=complete.message()		sender=complete[0].split(' ')		sender=sender[2]		return ["PRIVMSG $C$ :I am the !say module","PRIVMSG $C$ :Usage:","PRIVMSG $C$ :!say [input]"]